<script language="JavaScript"  type="text/javascript">function myconfirm(area_id, name) {	if(<?=$query->num_rows()?> < 2)		alert("There must be one area at least!");	else {		answer = confirm("Are you sure you want to delete area '" + name + "' ?");		if (answer) {			var form = document.getElementById('delete_form');			form.elements['area_id'].value = area_id;			form.submit();		}	}}</script><div id="title">Client ／ Area</div><table id="subTitle"><tr><th>Client: <?=$client['name']?>（<?=$client['account']?>）</th><td><a href="<?=site_url("admin/area/n/".$client['client_id']);?>">Add</a></td></tr></table><table id="list">	<tr>		<th>&nbsp;</th>		<th>Name</th>		<th>Country</th>		<th>Address</th>		<th width=50>Edit</th>		<th width=40>Delete</th>	</tr>	<?	$i=1;		foreach($query->result_array() as $row) {?>	<tr>		<td><?=$i++;?>.</td>		<td><a href="<?=site_url("admin/area_device/0/".$row['area_id']);?>"><?=$row['name']?></a></td>		<td><?=$row['country']?></td>		<td><?=$row['address']?></td>		<td><a href="<?=site_url('admin/area/e/'.$client['client_id'].'/'.$row['area_id']);?>">			<img src="<?=base_url('images/button/edit.png')?>"/></a>		</td>		<td><a href="javascript:myconfirm(<?=$row['area_id'];?>, '<?=$row['name']?>');">			<img src="<?=base_url('images/button/delete.png');?>"/></a>		</td>	</tr>	<?	} ?></table><form id="delete_form" method="post" action="<?=site_url("admin/area/d/".$client['client_id']);?>"><input type="hidden" name="area_id" value=""></form>