<script language="JavaScript"  type="text/javascript">function myconfirm(client_id, account) {	answer = confirm("Are you sure you want to delete sub account '" + account + "' ?");	if (answer) {		var form = document.getElementById('delete_form');		form.elements['client_id'].value = client_id;		form.submit();	}}</script><div id="title">Client ／ Sub-Account</div><table id="subTitle"><tr><th>Super Client: <?=$super['name']?>（<?=$super['account']?>）</th><td><a href="<?=site_url('admin/client_sub/n/'.$super['client_id'])?>">Add</a></td></tr></table><table id="list">	<tr>		<th>&nbsp;</th>		<th>Account</th>		<th>Name</th>		<th>Area Num</th>		<th>Insert Time</th>		<th width=50>Edit</th>		<th width=40>Delete</th>	</tr>	<?	$i=1;		if($query->num_rows() > 0)		foreach($query->result_array() as $row) {?>	<tr>		<td><?=$i++;?>.</td>		<td>			<a href="<?=site_url("admin/client_sub_area/".$row['client_id'])?>"><?=$row['account']?></a>		</td>		<td><?=$row['name']?></td>		<td><?=$row['area_num']?></td>		<td><?=$row['insert_time']?></td>		<td><a href="<?=site_url('admin/client_sub/e/'.$super['client_id']."/".$row['client_id']);?>">			<img src="<?=base_url('images/button/edit.png')?>"/></a>		</td>		<td><a href="javascript:myconfirm(<?=$row['client_id'];?>, '<?=$row['account']?>');">			<img src="<?=base_url('images/button/delete.png');?>"/></a>		</td>	</tr>	<?	} ?></table><form id="delete_form" method="post" action="<?=site_url("admin/client_sub/d");?>"><input type="hidden" name="client_id" value=""></form>